package algorithms

import (
	"strconv"
	"strings"
	"testing"
)

func TestLeftRotateString(t *testing.T) {
	cases := []struct {
		desc string
		s    string
		n    int
		want string
	}{
		{desc: "simple", s: "abcd", n: 2, want: "cdab"},
		{desc: "long", s: generateString(2000), n: 1000, want: ""},
	}

	for _, s := range cases {
		t.Run(s.desc, func(t *testing.T) {
			if got := leftRotateStringIterator(s.s, s.n); got != s.want {
				t.Errorf("s: %s, n: %d, want: %s, got: %s", s.s, s.n, s.want, got)
			}
			if got := leftRotateStringByStepThree(s.s, s.n); got != s.want {
				t.Errorf("s: %s, n: %d, want: %s, got: %s", s.s, s.n, s.want, got)
			}
		})
	}
}

func generateString(n int) string {
	b := strings.Builder{}
	for i := 0; i < n; i++ {
		b.WriteString(strconv.Itoa(i))
	}
	return b.String()
}

func BenchmarkLeftRotateStringIterator(b *testing.B) {
	s := generateString(2000)
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		leftRotateStringIterator(s, 1000)
	}
}

func BenchmarkLeftRotateStringByThreeStep(b *testing.B) {
	s := generateString(2000)
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		leftRotateStringByStepThree(s, 1000)
	}
}
